query Tag ($id: ID!, $page: Int) {
  tag: tag (id: $id) {
    title
    belongsTo (page: $page, perPage: 3) @paginate {
      totalCount
      pageInfo {
        totalPages
        currentPage
      }
      edges {
        node {
          __typename
          ...on News {
            title
    	      startDate: date (format: "DD MMM YYYY")
            path
            slug
            summary
            tags {
              title
            }
          }
          ...on Project {
             title
    	       startDate: startDate (format: "DD MMM YYYY")
             path
             slug
             summary
             tags {
              title
            }
          }
           ...on Publication {
             title
    	       startDate: date (format: "DD MMM YYYY")
             path
             slug
             summary
             tags {
              title
            }
          }
        }
      }
    }
  }
}
