.btn {
  background: none;
  border: none;

  display: block;
  width: 43px;
  height: 43px;

  position: relative;
  outline: none;

  @include mq($from: tablet) {
    display: none;
  }

  &--open {
    .svg--close {
      opacity: 0;
      transform: rotate(-180deg);
    }

    .svg--open {
      opacity: 1;
      transform: rotate(-180deg);
    }
  }
}

.svg {
  width: 30px;
  height: 30px;

  transition: transform 0.15s;

  position: absolute;
  top: calc(50% - 15px);
  left: calc(50% - 15px);

  &--open {
    opacity: 0;
  }
}
