import axios from 'axios';
export default {
  created() {
    axios.get(`${this.$static.metadata.pathPrefix}/search.json`).then(res => {
      this.posts = res.data;
    }).catch(error => {
      console.log(error);
    });
  },
  data() {
    return {
      query: '',
      results: [],
      posts: [],
      highlightedIndex: 0,
      searchResultsVisible: false,
      options: {
        shouldSort: true,
        includeMatches: true,
        threshold: 0.5,
        location: 0,
        distance: 500,
        maxPatternLength: 32,
        minMatchCharLength: 1,
        keys: ['title', 'summary']
      }
    };
  },
  methods: {
    reset() {
      this.query = '';
      this.highlightedIndex = 0;
    },
    softReset() {
      this.highlightedIndex = 0;
      this.searchResultsVisible = true;
    },
    performSearch() {
      this.$search(this.query, this.posts, this.options).then(results => {
        this.results = results;
      });
    },
    highlightPrev() {
      if (this.highlightedIndex > 0) {
        this.highlightedIndex = this.highlightedIndex - 1;
        this.scrollIntoView();
      }
    },
    highlightNext() {
      if (this.highlightedIndex < this.results.length - 1) {
        this.highlightedIndex = this.highlightedIndex + 1;
        this.scrollIntoView();
      }
    },
    scrollIntoView() {
      this.$refs.results.children[this.highlightedIndex].scrollIntoView({
        block: 'nearest',
        behavior: 'smooth'
      });
    },
    gotoLink() {
      const destPath = this.results[this.highlightedIndex].item.path;
      const currentPath = this.$route.path;
      if (destPath === currentPath) {
        this.reset();
        this.$refs.search.blur();
      } else if (destPath) {
        this.$router.push(destPath).then(() => {
          this.reset();
          this.$refs.search.blur();
        });
      }
    }
  }
};
